mapper.select:
       select top ${pageSize} 店铺名称 as shopName,sum(销售金额) as xsje ,sum(目标金额) as mbje
       from cwbz_xiaoshou where 店铺名称 not in ( select   top ${pageStart} 店铺名称 from [cwbz_xiaoshou])
       and 日期 between cast('${start}' as datetime) and cast('${end}' as datetime)

           and 店铺名称=#{shopName}

	 group by 店铺名称
