mapper.select:
select 总监名称 from kehu where 1 = 1

	and 总监名称 = #{name}

group by 总监名称

select 经理名称 from kehu where 1 = 1

	and 经理名称 = #{name}

group by 经理名称

select 督导名称 from kehu where 1 = 1

	and 督导名称 = #{name}

group by 督导名称

select   商店名称 from kehu where 1 = 1

	and 商店名称 = #{name}

group by 商店名称

select 顾客名字, 顾客手机, SUM(金额) as 金额, SUM(数量) as 数量 from cwbz_xsmx_hy where 日期 between '${start}' and DATEADD(DAY,1,'${end}') and LEN(VIP卡号) >0
and 店铺名称 in (${shopName})
group by 顾客名字,顾客手机 order by 金额 desc;

select COUNT(0) from cwbz_mrkc
	where
	 类型='店铺' and 日期 = #{end}

select 督导名称, sum(金额) as 金额, sum(数量) as 数量 from cwbz_xsmx_hy where 1 = 1 and 日期 between '${start}' and DATEADD(DAY,1,'${end}')
and 地区名称 = #{area}
group by 督导名称

select 店铺名称, sum(金额) as 金额, sum(数量) as 数量 from cwbz_xsmx_hy where 1 = 1 and 日期 between '${start}' and DATEADD(DAY,1,'${end}')
and 地区名称 = #{area}
group by 店铺名称
